                    {/* <FormLabel component="legend">Touch Screen</FormLabel> */}
              
                    <p>  Does the Laptop switch on? </p>
      <p>  See what this means?</p>
      
                    <RadioGroup value={inputData} onChange={handleChange5}>
                        <label >
                            <span  aria-disabled="false">
                            <div className="row">
                          
                                <div className="features-box">
                                    <FormControlLabel value="yes" style={{width:500,color:'black'}} control={<Radio/>} label="yes" />
                                    </div>
                                <div className="features-box">
                                    <FormControlLabel value="No"   style={{width:500,color:'black'}} control={<Radio onClick={addItems} />} label="no" />
                                     </div>
                                   
                            </div>
                            </span>
                        </label>
                    </RadioGroup>
                </FormControl>
                </Card>
                </div>
                </div>


                {/*<NavLink to='/systemconfiguration'>*/}






























